mapper.select:
select cp.pkg_name,c.name as chapter_name,p.name,p.video_file,p.begin from buy_record br inner join course_pkg cp on br.course_pkg_id=cp.uid inner join chapter c on cp.uid=c.course_id inner join period p on c.uid=p.chapter_id where br.tenant_id=#{tenant_id} and br.student_id=#{uid} and cp.status="1"
and cp.course_type_id=#{course_type_id}
and cp.pkg_subject_id=#{pkg_subject_id}
and p.begin like CONCAT(#{now_time},'%')
and cp.play_type_id=#{play_type_id}
group by cp.uid

select cp.cover,cp.uid,cp.pkg_name,cp.pkt_type_id,cp.total_price,t.nickname,p.begin
from buy_record br
inner join course_pkg cp on br.course_pkg_id=cp.uid
inner join teacher t on cp.teacher_id=t.uid
inner join chapter c on cp.uid=c.course_id
inner join period p on c.uid=p.chapter_id
where br.student_id=#{uid}
and br.tenant_id=#{tenant_id}
and cp.status="1"

    and cp.pkg_subject_id=#{pkg_subject_id}


    and cp.play_type_id=#{play_type_id}


    and cp.pkt_type_id=#{pkt_type_id}

SELECT b.uid AS uid, c.pkg_name AS pkg_name, c.total_price AS total_price, b.time AS time FROM buy_record b inner JOIN course_pkg c ON b.course_pkg_id = c.uid WHERE b.student_id =#{uid} AND b.tenant_id =#{tenant_id} and c.status="1"
AND b.time >= #{begin_time}
AND b.time <= #{over_time}
