mapper.select:
select cp.cover, cp.uid, cp.pkg_name, cp.play_type_id, t.nickname, pl.name as level_name, pt.name as pkg_type_name from course_pkg cp left join teacher t on cp.teacher_id=t.uid left join pkg_level pl on cp.pkg_level_id=pl.uid left join pkg_subject ps on cp.pkg_subject_id=ps.uid left join pkg_type pt on pt.uid=cp.course_type_id where cp.tenant_id=#{tenant_id} and cp.status="1" and cp.pkt_type_id="1"
and cp.pkg_subject_id=#{pkg_subject_id}
limit 0,6

select cp.cover, cp.uid, cp.pkg_name, cp.total_price, t.nickname, p.begin from course_pkg cp inner join chapter c on cp.uid=c.course_id inner join period p on p.chapter_id=c.uid inner join teacher t on t.uid=cp.teacher_id where cp.tenant_id=#{tenant_id} and cp.status="1"
and cp.pkt_type_id=#{pkt_tyepe_id}
and cp.is_goods=${is_goods}
and cp.pkg_subject_id=#{pkg_subject_id}
and cp.pkg_grade_id=#{pkg_grade_id}
and cp.play_type_id='play_type_id'
and cp.course_type_id='course_type_id'
order by p.begin asc
order by p.begin desc
order by cp.total_price desc
order by cp.total_price asc

SELECT c.uid AS uid, c.pkg_name AS pkg_name, c.cover AS cover, c.play_type_id AS play_type_id, t.nickname AS nickname, c.apply_number AS apply_number, c.total_price AS total_price, pn.comments_star AS comments_star, p.`name` AS subject_name, pn.praise_number AS praise_number FROM course_pkg c LEFT JOIN teacher t ON c.teacher_id = t.uid LEFT JOIN pkg_subject p ON c.pkg_subject_id = p.uid LEFT JOIN period_evaluation pn ON c.uid = pn.course_id WHERE c.tenant_id =#{tenant_id} and c.status="1"
AND c.play_type_id =#{play_type_id}
AND c.pkg_grade_id =#{pkg_grade_id}
AND c.pkg_subject_id =#{pkg_subject_id}
AND c.course_type_id =#{course_type_id}
AND c.is_index =#{is_index}
AND c.pkt_type_id =#{pkt_type_id}
AND c.apply_number =#{apply}
AND c.pkg_name like CONCAT('%',#{key},'%')

select cp.uid,cp.pkg_name,cp.play_type_id,p.name,c.name as chapter_name,p.video_file,p.begin
from course_pkg cp left join chapter c on cp.uid=c.course_id
left join period p on p.chapter_id=c.uid
where cp.teacher_id=#{uid} and cp.tenant_id=#{tenant_id} and cp.status="1"

	and cp.play_type_id=#{play_type_id}


	and cp.course_type_id=#{course_type_id}


	and cp.pkg_subject_id=#{pkg_subject_id}


	and p.begin like CONCAT(#{now_time},'%')
