mapper.select:
SELECT pd.uid AS uid, cg.pkg_name AS pkg_name, pd.`name` AS `name`, cr.`name` AS chapter_name, pd.video_file AS video_file, pd.`begin` AS `begin`
FROM period pd left JOIN chapter cr ON pd.chapter_id = cr.uid left JOIN course_pkg cg ON cr.course_id = cg.uid left JOIN buy_record brd ON cg.uid = brd.course_pkg_id left JOIN student st ON brd.student_id = st.uid WHERE st.uid = #{uid} and st.tenant_id=#{tenant_id} and cg.status="1"
and cg.play_type_id=#{play_type_id}

SELECT p.uid, cg.pkg_name, p.`name`, cer.`name` AS chapter_name, p.`begin`, p.video_file FROM period p LEFT JOIN chapter cer ON p.chapter_id = cer.uid LEFT JOIN course_pkg cg ON cer.course_id = cg.uid LEFT JOIN teacher t ON cg.teacher_id = t.uid where t.uid=#{uid} and t.tenant_id=#{tenant_id}
and cg.play_type_id=#{play_type_id}
order by p.`begin`
