mapper.select:

where course_pkg.tenant_id=#{tenantId} and course_pkg.pkt_type_id="1"

    and course_pkg.pkg_subject_id='1'


    and course_pkg.pkg_subject_id=#{pkg_subject_id}


    and course_pkg.pkg_grade_id='1'


    and course_pkg.pkg_grade_id=${pkg_grade_id}

select cp.cover, cp.uid, cp.pkg_name, cp.total_price, t.nickname, p.begin from course_pkg cp inner join chapter c on cp.uid=c.course_id inner join period p on p.chapter_id=c.uid inner join teacher t on t.uid=cp.teacher_id where 1=1 and cp.tenant_id=#{tenantId}
and cp.pkt_type_id=#{pkt_tyepe_id}
and cp.pkg_subject_id=#{pkg_subject_id}
and cp.pkg_grade_id=#{pkg_grade_id}
and cp.play_type_id='play_type_id'
and cp.course_type_id='course_type_id'
order by p.begin asc
order by p.begin desc
order by cp.total_price desc
order by cp.total_price asc

select a.uid, a.intr, a.back_img, a.nickname, a.integral, a.subject_name, a.grade_name, a.cover, a.pkg_name, a.begin, a.school_name, b.course_count from (select t.uid, t.intr, t.back_img, t.nickname, t.integral, ps.name as subject_name, pg.name as grade_name, cp.cover, cp.pkg_name, p.begin, s.school_name from teacher t inner join course_pkg cp on t.uid=cp.teacher_id inner join pkg_subject ps on ps.uid=cp.pkg_subject_id inner join school s on t.school_id=s.uid inner join chapter ch on ch.course_id=cp.uid inner join period p on p.chapter_id=ch.uid inner join pkg_grade pg on pg.uid=cp.pkg_grade_id where t.tenant_id=#{tenantId}
and cp.pkg_subject_id=#{pkg_subject_id}
and cp.pkg_grade_id=#{pkg_grade_id}
order by p.begin asc
order by p.begin desc
order by t.integral asc
order by t.integral desc
) as a, (select count(t.uid) as course_count from course_pkg cp INNER JOIN teacher t on cp.teacher_id=t.uid where cp.tenant_id=#{tenantId})as b

select
  uid,intr,culture,school_name,banner
from school where tenant_id=#{tenantId}

select
  cp.pkg_name,c.name as chapter_name,p.name,p.video_file,p.begin
from buy_record br
  left join course_pkg cp on br.course_pkg_id=cp.uid
  left join chapter c on cp.uid=c.course_id
  left join period p on c.uid=p.chapter_id
where br.tenant_id=#{tenantId} and br.student_id=#{uid}

    and cp.course_type_id=#{course_type_id}


    and cp.pkg_subject_id=#{pkg_subject_id}

  and c.bgin like CONCAT(#{now_time},'%')

    and cp.play_type_id=#{play_type_id}

select an.answer_file_name,an.answer_time,an.answer,an.answer_file_url,an.uid, ask.question,ask.question_attach,ask.question_url, p.video_file,p.begin,p.name, c.name as chapter_name, cp.pkg_name, t.nickname as teacher_nickname, s.nickname from answer an left join course_ask ask on an.ask_id=ask.uid left join period p on an.period_id=p.uid left join chapter c on p.chapter_id=c.uid left join course_pkg cp on c.course_id=cp.uid left join teacher t on cp.teacher_id=t.uid left join student s on an.student_id=s.uid where an.student_id=#{uid} and an.tenant_id=#{tenantId}
and an.type=1
and an.type=2

select cp.cover,cp.uid,cp.pkg_name,cp.pkt_type_id,cp.total_price,t.nickname,p.begin from buy_record br
  left join course_pkg cp on br.course_pkg_id=cp.uid
  left join teacher t on cp.teacher_id=t.uid
  left join chapter c on cp.uid=c.course_id
  left join period p on c.uid=p.chapter_id
where br.student_id=#{uid}
  and br.tenant_id=#{tenantId}

      and cp.pkg_subject_id=#{pkg_subject_id}


    and cp.play_type_id=#{play_type_id}


  and cp.pkt_type_id=#{pkt_type_id}
